function n(){const c=document.getElementById("character-img"),a=document.querySelectorAll(".main-characters__character");function r(t){fetch(`https://rickandmortyapi.com/api/character/?name=${t}`).then(e=>e.json()).then(e=>{e.results&&e.results[0]&&(c.src=e.results[0].image,c.alt=t)}).catch(e=>console.error("Error loading character image:",e))}r("Rick Sanchez"),a.forEach(t=>{t.addEventListener("click",()=>{const e=t.getAttribute("data-name");r(e)})})}document.addEventListener("DOMContentLoaded",()=>{n()});
